$('textarea').on('keyup',function(){
        if(this.id != 'json')
        {
          $('#json').val(JSON.stringify({
            'html': $('[name=html]').val(),
            'css': $('[name=css]').val(),
            'directjs': $('[name=directjs]').val(),
            'worker': $('[name=worker]').val(),
            'dependencies': $('[name=dependencies]').val().split(','),
            'name': $('[name=name]').val()}));
        }
        else
        {
          for(var j = JSON.parse(this.value), i = 0, k = Object.keys(j); i<k.length; i++)
          {
            $('[name='+k[i]+']').val(is_string(j[k[i]]) ? j[k[i]] : j[k[i]].join(','));
          }
        }
      });
